mapper.select:
SELECT member_id, username, email, password, phone_number
FROM members
WHERE members.member_id = #{id}
ORDER BY
member_id ASC;

mapper.insert:
INSERT INTO members(username, email, password, phone_number)
VALUES(#{username},
#{email}, #{passwordHash}, #{phoneNumber});

mapper.update:
UPDATE members

	username = #{username},
	phone_number = #{phoneNumber},
	password = #{passwordHash}

WHERE member_id = #{id}
