mapper.select:
SELECT member_id, username, email, password, phone_number
FROM members
WHERE members.member_id = #{id}
ORDER BY member_id ASC;

mapper.insert:
INSERT INTO members(username, email, password, phone_number)
VALUES(#{username},
#{email}, #{password}, #{phoneNumber});

mapper.update:
UPDATE members
SET username = #{username}, email = #{email},
password = #{password}, phone_number = #{phoneNumber}
WHERE member_id = #{id}
